def translate_file(filepath):
    with open(filepath, 'r', encoding='utf-8') as f:
        content = f.read()
    
    new_content = content
    
    # Sort keys by length descending to replace longest matches first (Crucial!)
    sorted_keys = sorted(translations.keys(), key=len, reverse=True)
    
    for en_text in sorted_keys:
        ru_text = translations[en_text]
        
        # Escape the key to treat it as literal text
        pattern_str = re.escape(en_text)
        # Replace escaped spaces with \s+ to match any whitespace (spaces, newlines, tabs)
        pattern_str = pattern_str.replace(r'\ ', r'\s+')
        
        # Use re.sub with regex pattern
        new_content = re.sub(pattern_str, ru_text, new_content)
        
    if new_content != content:
        print(f"Applying full translation to: {os.path.basename(filepath)}")
        with open(filepath, 'w', encoding='utf-8') as f:
            f.write(new_content)

def run():
    # 1. Services directory
    if os.path.exists(target_dir):
        for f in os.listdir(target_dir):
            if f.endswith(".html"):
                translate_file(os.path.join(target_dir, f))
    
    # 2. Root directory files (doctors.html, index.html etc already partially done but good to re-run)
    if os.path.exists(root_dir):
        for f in os.listdir(root_dir):
            if f.endswith(".html"):
                translate_file(os.path.join(root_dir, f))

if __name__ == "__main__":
    run()
